import './Board.css';
import Tile from '../Tile/Tile';

const horizontalAxis = Array.apply(null, Array(10)).map(function(currentValue, index) { return index;});
const verticalAxis = Array.apply(null, Array(10)).map(function(currentValue, index) { return index;});

const pieces = [];
let valuex = (Math.random()%8);
let valuey = (Math.random()%8);
pieces.push({image: "assets/images/b_pawn.png", x: 0, y: 1})

export default function Board() {
    let board = [];

    for (let j = verticalAxis.length - 1; j >= 0; j--) {
        for (let i = 0; i < horizontalAxis.length; i++) {
            const number = j + i + 2;
            let image = undefined;

            pieces.forEach(p => {
                if(p.x === i && p.y === j) {
                    image = p.image;
                }
            });

            board.push(<Tile number={number} image={image}/>);

        }
    }

    return <div id = "Board">{board}</div>;
}
